import { styled, useTheme } from '@mui/material/styles';
import React, { useState } from 'react';
import ListItemButton from '@mui/material/ListItemButton';
import Box from '@mui/material/Box';
import MuiDrawer from '@mui/material/Drawer';
import MuiAppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import List from '@mui/material/List';
import CssBaseline from '@mui/material/CssBaseline';
import Typography from '@mui/material/Typography';
import Divider from '@mui/material/Divider';
import LoginIcon from '@mui/icons-material/Login';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import QuestionMarkIcon from '@mui/icons-material/QuestionMark';
import BallotIcon from '@mui/icons-material/Ballot';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import { makeStyles } from '@material-ui/core/styles';
import '../Styles/students.css';
import styledt from 'styled-components';
import image from "../Images/std.png";
import { Helmet } from 'react-helmet'


const drawerWidth = 280;

const TITLE = 'Estudiantes'

const StyledBody = styledt.div`
  background: url(${image});  
  background-size: cover;
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  width: 100%;
  height: 100%;
`;


const useStyles = makeStyles(theme => ({
	root: {
		backgroundSize: 'auto',
		backgroundPosition: 'center',
		height: '100vh',
	},
	typography: {
		color: "#606060",
		fontFamily: 'Open Sans Condensed',
	},

}));


const openedMixin = (theme) => ({
	width: drawerWidth,
	transition: theme.transitions.create('width', {
		easing: theme.transitions.easing.sharp,
		duration: theme.transitions.duration.enteringScreen,
	}),
	overflowX: 'hidden',
});

const closedMixin = (theme) => ({
	transition: theme.transitions.create('width', {
		easing: theme.transitions.easing.sharp,
		duration: theme.transitions.duration.leavingScreen,
	}),
	overflowX: 'hidden',
	width: `calc(${theme.spacing(7)} + 1px)`,
	[theme.breakpoints.up('sm')]: {
		width: `calc(${theme.spacing(9)} + 1px)`,
	},
});


const DrawerHeader = styled('div')(({ theme }) => ({
	display: 'flex',
	alignItems: 'center',
	justifyContent: 'flex-end',
	padding: theme.spacing(0, 1),
	// necessary for content to be below app bar
	...theme.mixins.toolbar,
}));

const AppBar = styled(MuiAppBar, {
	shouldForwardProp: (prop) => prop !== 'open',
})(({ theme, open }) => ({
	zIndex: theme.zIndex.drawer + 1,
	transition: theme.transitions.create(['width', 'margin'], {
		easing: theme.transitions.easing.sharp,
		duration: theme.transitions.duration.leavingScreen,
	}),
	...(open && {
		marginLeft: drawerWidth,
		width: `calc(100% - ${drawerWidth}px)`,
		transition: theme.transitions.create(['width', 'margin'], {
			easing: theme.transitions.easing.sharp,
			duration: theme.transitions.duration.enteringScreen,
		}),
	}),
}));

const Drawer = styled(MuiDrawer, { shouldForwardProp: (prop) => prop !== 'open' })(
	({ theme, open }) => ({
		width: drawerWidth,
		flexShrink: 0,
		whiteSpace: 'nowrap',
		boxSizing: 'border-box',
		...(open && {
			...openedMixin(theme),
			'& .MuiDrawer-paper': openedMixin(theme),
		}),
		...(!open && {
			...closedMixin(theme),
			'& .MuiDrawer-paper': closedMixin(theme),
		}),
	}),
);


export default function Home() {
	const theme = useTheme();
	const [open, setOpen] = React.useState(false);
	//const [body, setBody] = useState({ username: Login.username, password: Login.password })
	//console.log("handleChange "+ Login.username);
	const classes = useStyles()



	const handleSubmit = async (e) => {


	};


	const handleDrawerOpen = () => {
		setOpen(true);
	};

	const handleDrawerClose = () => {
		setOpen(false);
	};

	return (
		<StyledBody>
			<Helmet><title>{TITLE}</title> </Helmet>
			<Box sx={{ display: 'flex' }} >
				<CssBaseline />
				<AppBar position="fixed" open={open} style={{ background: '#F6E064' }}>
					<Toolbar style={{ background: '#F6E064' }}>
						<IconButton
						style={{ background: '#F6E064' }}
							aria-label="open drawer"
							onClick={handleDrawerOpen}
							edge="start"
							sx={{
								marginRight: '36px',
								...(open && { display: 'none' }),
							}}
						>
							<MenuIcon style={{ color: '#606060' }} />
						</IconButton>
						<Typography variant="h5" noWrap component="div" className={classes.typography}>
							Estudiantes
						</Typography>
					</Toolbar>
				</AppBar>
				<Drawer variant="permanent" open={open} >
					<DrawerHeader style={{ background: '#F6E064' }}>
						<IconButton onClick={handleDrawerClose}>
							{theme.direction === 'rtl' ? <ChevronRightIcon style={{ background: '#F6E064' }} /> : <ChevronLeftIcon style={{ background: '#F6E064' }}/>}
						</IconButton>
					</DrawerHeader>
					<Divider />
					<List >
						<ListItemButton onClick={(e) => handleSubmit(e)}>
							<ListItemIcon ><AccountCircleIcon style={{ color: '#606060' }} /></ListItemIcon>
							<ListItemText primary="Administrar perfil" /></ListItemButton>
						<ListItemButton>
							<ListItemIcon>
								<BallotIcon style={{ color: '#606060' }} />
							</ListItemIcon>
							<ListItemText primary="Informes de asistencia" />
						</ListItemButton>
						<ListItemButton>
							<ListItemIcon>
								<QuestionMarkIcon style={{ color: '#606060' }} />
							</ListItemIcon>
							<ListItemText primary="Marcar asistencia" />
						</ListItemButton>
						<ListItemButton>
							<ListItemIcon>
								<LoginIcon style={{ color: '#606060' }} />
							</ListItemIcon>
							<ListItemText primary="Cerrar sesiÃ³n" />
						</ListItemButton>
					</List>
					<Divider />
				</Drawer>
			</Box>
		</StyledBody>
	);
}
