import React, { Fragment, useState } from "react";


const myFunction = async () => {
  const { value: formValues } = await Swal.fire({
    title: "Multiple inputs",
    html:
      '<input id="swal-input1" placeholder="Identificación" class="swal2-input">' +
      '<input id="swal-input2" placeholder="Nombre" class="swal2-input">' +
      '<input id="swal-input3" type = "password" placeholder="Password" class="swal2-input">' +
      '<input id="swal-input4" placeholder="Email" class="swal2-input">' +
      '<input id="swal-input5" placeholder="Description" class="swal2-input">',
    focusConfirm: false,
    preConfirm: () => {
      return [
        document.getElementById("swal-input1").value,
        document.getElementById("swal-input2").value,
        document.getElementById("swal-input3").value,
        document.getElementById("swal-input4").value,
        document.getElementById("swal-input5").value
      ];
    },
  });

  if (formValues) {
    Swal.fire('Datos ingresados');
    try {

      const data = {
        identificacion: formValues[0], 
        nombre: formValues[1], 
        password: formValues[2], 
        email: formValues[3], 
        description: formValues[4]
      }

      const response = await fetch('https://attendancjyc-backend.herokuapp.com/administrador/', {
        method: 'POST', // or 'PUT'
        body: JSON.stringify(data), // data can be `string` or {object}!
        headers:{
          'Content-Type': 'application/json'
        }
      });

      if(response.json){
        Swal.fire('Administrador agregado con Exito');
      }
      else{
        Swal.fire('Administrador no pudo ser agregado');
      }

    } catch (e) {
      
    }
  }
};


const EditAdmin = (props) => {

  const [nombre, setNombre] = useState(props.todo.nombre ? props.todo.nombre : 'Juan');

  let [actiModal, setActiveModal] = useState(false);

  //edit description function

  const onOffModal = () => {
    setActiveModal(!actiModal)
  }

  const updateNombre= async e => {
    e.preventDefault();
    try {
      const body = {
        nombre: nombre,
        precio: props.todo.precio, 
        descripcion: props.todo.descripcion, 
        categoria: props.todo.categoria, 
        cantidad: props.todo.cantidad_exitente_producto
      };
      console.log(body)
      await fetch(
        `https://provo-backend.herokuapp.com/productos/${props.todo.id}`,
        {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(body)
        }
      ).then(() => {
        window.location = "/productos";
      });

      //
    } catch (err) {
      console.error(err.message);
    }
  };

  return (
    <Fragment>
      <button
        type="button"
        className="btn btn-warning"
        data-toggle="modal"
        data-target={`#id${props.todo.id}`}
        onClick={onOffModal}
      >
        Edit
      </button>

      <div
        className={actiModal ? 'modalContainerProduct' : "modalOff"}
        id={`id${props.todo.id}`}
        onClick={() => setNombre(props.todo.nombre)}
      >
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h4 className="modal-title">Edit Producto</h4>
              <button
                type="button"
                className="close"
                data-dismiss="modal"
                onClick={() => onOffModal()}
              >
                &times;
              </button>
            </div>

            <div className="modal-body">
              <input
                type="text"
                className="form-control"
                value={nombre}
                onChange={e => setNombre(e.target.value)}
              />
            </div>

            <div className="modal-footer">
              <button
                type="button"
                className="btn btn-warning"
                data-dismiss="modal"
                onClick={e => {
                    updateNombre(e)
                    onOffModal()
                  }
                }
              >
                Edit
              </button>
              <button
                type="button"
                className="btn btn-danger"
                data-dismiss="modal"
                onClick={onOffModal}
              >
                Close
              </button>
            </div>
          </div>
        </div>
      </div>
    </Fragment>
  );
};

export default EditAdmin;
